import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css'; 

function Home() {
  return (
    <div className="home">
      <div className="container">
        <Link to="/peliculas" className="box peliculas" style={{ backgroundImage: `url(${process.env.PUBLIC_URL + 'https://png.pngtree.com/element_our/20190603/ourlarge/pngtree-movie-board-icon-image_1455346.jpg'})` }}>
          <h2 style={{ textAlign: 'center', fontFamily: 'Arial, sans-serif' }}>Películas</h2>
        </Link>
        <div className="description">PELÍCULAS</div>
      </div>

      <div className="container">
        <Link to="/series" className="box series" style={{ backgroundImage: `url(${process.env.PUBLIC_URL + 'https://png.pngtree.com/element_our/20190603/ourlarge/pngtree-movie-board-icon-image_1455346.jpg'})` }}>
          <h2 style={{ textAlign: 'center', fontFamily: 'Arial, sans-serif' }}>Series</h2>
        </Link>
        <div className="description">SERIES</div>
      </div>
    </div>
  );
}

export default Home;
